
public class Sandbox {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 0, a = 0;
		
		for(i=0; i<10; i++) {
			a = i*2;
			System.out.print(a+ " ");
		}
		System.out.println();
		for(i=0; i<10; i++) {
			System.out.print(a+ " ");
		}
	}

}
